<template>
  <div class="card image-card">
    <slot name="image"></slot>
    <div class="card-body">
      <slot name="body">
        <h5 v-if="title" class="card-title">{{ title }}</h5>
        <p v-if="description" class="card-text">{{ description }}</p>
        <p v-if="linkUrl" class="card-text">
          <small class="text-muted">
            <a :href="linkUrl" class="stretched-link">{{ linkText }} </a>
          </small>
        </p>
      </slot>
    </div>
  </div>
</template>

<style>
/* 
 * Work around for not being able to set class card-img-top on slot
 * https://vuejs.org/v2/guide/migration.html#slot-Attribute-Styling-removed
 * 
 * here we can use the context to reset the destroyed style  
 *
 */
.image-card > img {
  width: 100%;
  border-top-left-radius: calc(0px - 1px);
  border-top-right-radius: calc(0px - 1px);
}

.card-group,
.card-columns > .image-card > img {
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
  border-bottom-left-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
}
</style>
<script>
export default {
  name: "ImageCard",
  props: {
    title: {
      type: String,
      required: false,
    },
    description: {
      type: String,
      required: false,
    },
    linkUrl: {
      type: String,
      required: false,
    },
    linkText: {
      type: String,
      required: false,
      default: () => "LEARN MORE",
    },
  },
};
</script>

